import React, {useState} from "react";

function CreateArea() {

    const [notes, setNotes] = useState({
        title: "",
        content: ""
    }); 

    function handleChange(event){

        const {value, name} = event.target;

        setNotes(() => {
            name === "title" ? value = notes.title : value = notes.content;
        })
    }

    console.log(notes);


  return (
    <div>
      <form>
        <input name="title" placeholder="Title" value={notes.title} />
        <textarea name="content" placeholder="Take a note..." rows="3" value={notes.content} />
        <button onClick={handleChange}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
